import axios from 'axios';

const dummyUser = {
  id: 1,
  nickname: '제로초',
  Posts: [],
  Followings: [],
  Followers: [],
};

export const initialState = {
  isLoggingIn: false, // 로그인 시도중
  isLoggedIn: false,
  isLoggingOut: false, // 로그아웃 시도중
  me: null,
  signUpData: {},
  loginData: {},
};

export const SIGN_UP = 'SIGN_UP';
export const SIGN_UP_SUCCESS = 'SIGN_UP_SUCCESS';
export const LOG_IN_REQUSET = 'LOG_IN_REQUSET'; // 액션의 이름
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS'; // 액션의 이름
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE'; // 액션의 이름
export const LOG_OUT_REQUSET = 'LOG_OUT_REQUSET';
export const LOG_OUT_SUCCESS = 'LOG_OUT_SUCCESS';
export const LOG_OUT_FAILURE = 'LOG_OUT_FAILURE';

export const loginAction = (data) => {
  return (dispatch) => {
    const state = getState();
    dispatch(loginRequsetAction())
    axios.get('/api/login')
      .then((res) => {
        dispatch(loginSuccessAction(res.data));
      })
      .catch((err) => {
        dispatch(loginFailureAction(err));
      })
  }
}

export const signUpAction = (data) => {
  return {
    type: SIGN_UP,
    data,
  };
};

export const signUpSuccess = {
  type: 'SIGN_UP_SUCCESS',
};

export const loginRequsetAction = (data) => {
  return {
    type: 'LOG_IN_REQUSET',
    data,
  }
};

export const loginSuccessAction = (data) => {
  return {
    type: 'LOG_IN_SUCCESS',
    data,
  }
};

export const loginFailureAction = (data) => {
  return {
    type: 'LOG_IN_FAILURE',
    data,
  }
};

export const logoutRequsetAction = (data) => {
  return {
    type: 'LOG_OUT_REQUSET',
    data,
  }
};

export const logoutSuccessAction = (data) => {
  return {
    type: 'LOG_OUT_SUCCESS',
    data,
  }
};

export const logoutFailureAction = (data) => {
  return {
    type: 'LOG_OUT_FAILURE',
    data,
  }
};




export const signUp = (data) => {
  return {
    type: SIGN_UP,
    data,
  }
};

export default (state = initialState, action) => {
  switch (action.type) {
    case 'LOG_IN_REQUSET': {
      return {
        ...state,
        isLoggingIn: true,
      };
    }
    case 'LOG_IN_SUCCES': {
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: true,
        me: action.data,
      };
    }
    case 'LOG_IN_FAILURE': {
      return {
        ...state,
        isLoggingIn: false,
        isLoggedIn: false,
      };
    }
    case 'LOG_OUT_REQUSET': {
      return {
        ...state,
        isLoggingOut: true,
        isLoggedIn: false,
      };
    }
    case 'LOG_OUT_SUCCESS': {
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: false,
        me: null,
      };
    }
    case 'LOG_OUT_FAILURE': {
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: false,
      };
    }
    case SIGN_UP: {
      return {
        ...state,
        signUpData: action.data,
      };
    }
    default: {
      return {
        ...state,
      }
    }
  }
};
